INSERT INTO `pages_pages` (`page_page_id`, `title`, `text`, `page_id`) VALUES
(4, 'Главная страница', '<p>\n	&nbsp;</p>\n<h2 style="color: rgb(0, 0, 0); background-image: none; margin: 0px 0px 8px; overflow: hidden; padding: 0px; border: 0px; font-size: 17px; font-family: sans-serif; line-height: 19.200000762939453px;">\n	<span class="mw-headline" id=".D0.98.D0.B7.D0.B1.D1.80.D0.B0.D0.BD.D0.BD.D0.B0.D1.8F_.D1.81.D1.82.D0.B0.D1.82.D1.8C.D1.8F">Избранная статья&nbsp;</span></h2>\n<div id="mf-tfa" style="color: rgb(0, 0, 0); font-family: sans-serif; font-size: 13px; line-height: 19.200000762939453px;">\n	<div class="floatright" style="clear: right; float: right; position: relative; margin: 0px 0px 0.5em 0.5em; border: 0px;">\n		<a class="image" href="http://commons.wikimedia.org/wiki/File:Borgia_Apartment_003.jpg?uselang=ru" style="text-decoration: initial; color: rgb(11, 0, 128); background-image: none; background-position: initial initial; background-repeat: initial initial;" title="Арифметика. Роспись Пинтуриккьо. Апартаменты Борджиа. 1492—1495. Рим, Ватиканские дворцы"><img alt="Арифметика. Роспись Пинтуриккьо. Апартаменты Борджиа. 1492—1495. Рим, Ватиканские дворцы" height="167" src="http://upload.wikimedia.org/wikipedia/commons/thumb/a/a2/Borgia_Apartment_003.jpg/150px-Borgia_Apartment_003.jpg" srcset="//upload.wikimedia.org/wikipedia/commons/thumb/a/a2/Borgia_Apartment_003.jpg/225px-Borgia_Apartment_003.jpg 1.5x, //upload.wikimedia.org/wikipedia/commons/thumb/a/a2/Borgia_Apartment_003.jpg/300px-Borgia_Apartment_003.jpg 2x" style="border: none; vertical-align: middle;" width="150" /></a></div>\n	История арифметики охватывает период от возникновения счёта до формальногоопределения чисел и арифметических операций над ними с помощью системыаксиом. Арифметика &mdash; наука о числах, их свойствах и <a href="/media/kcfinder/upload/files/vozmi.swf" target="_blank">отношениях </a>&mdash; является одной из основных математических наук. Она тесно связана с алгеброй и теорией чисел. Причиной возникновения арифметики стала практическая потребность в счёте, простейших измерениях и вычислениях. Первые достоверные сведения об арифметических знаниях обнаружены в исторических памятниках Вавилона иДревнего Египта, относящихся к III&mdash;II тысячелетиям до н. э. Большой вклад в развитие арифметики внесли греческие математики, в частности пифагорейцы, которые пытались с помощью чисел определить все закономерности мира. В Средние века основными областями применения арифметики были торговля и приближённые вычисления. Арифметика развивалась в первую очередь в Индии и странах ислама и только затем пришла в Западную Европу. В XVII веке мореходнаяастрономия, механика, более сложные коммерческие расчёты поставили перед арифметикой новые запросы к технике вычислений и дали толчок к дальнейшему развитию. (далее&hellip;)</div>\n<p>\n	&nbsp;</p>\n', 51);

